    public static boolean existeP(String cadena){
        boolean res;
        res=false;
        for(int i=0;i<cadena.length();i++){
            if(cadena.substring(i,i+1).equals(".")){
                res=true;
                break;
            }      
        }
        return res;
    }
    public static String pruebaEval(String exprStr) {

        RevisaSintaxisExpre rp1;
        boolean esCrrct;
        String resEval;
        String Infix[], Postfix[];

        System.out.println("---- PRIMER PASO: Revision de Sintaxis  ----");        
        rp1 = new RevisaSintaxisExpre();
        esCrrct = rp1.sintaxisExpre(exprStr);
        resEval= rp1.getErrorMsg();
        System.out.println("    >>Revisiรณn INFIX String: " + resEval );
        
        System.out.print("    ==Expresion INFIX [] String separada en Tokens: ,");
        Infix= rp1.getArrStr();
        for( String a: Infix) {  // foreach
            System.out.print(a + ",");
        }        
        
        double eval= 0.0;
        if(esCrrct) {
            // Infix to Postfix
            System.out.println("---- TERCER PASO: notacion Infix to Postfix ----");        
            InfixToPostfix inpo = new InfixToPostfix();
            Postfix = inpo.convInfixPostfix(Infix);
            System.out.print("     Postfix");
            for( String a: Postfix) {  // foreach
                System.out.print(" " + a);
            }
            System.out.println();

            esCrrct = inpo.convOK();
            if(esCrrct) {
                // Postfix Evaluator
                System.out.println("---- CUARTO PASO: Postfix Evaluator ----");        
                PostfixEvaluator pfexpre = new PostfixEvaluator();
                eval = pfexpre.evaluate(Postfix);
                System.out.println("     Evaluaciรณn (double): " + eval +"\n");
                resEval= "" + eval;  // convirtiendo double a String
            }            
        }   
        return resEval;
    }
    
